import socket
from time import sleep

header_raw = ''
header_bytes = bytes.fromhex(header_raw)

body_raw = ''
body_bytes = bytes.fromhex(body_raw)

with socket.socket(socket.AF_INET, socket.SOCK_STREAM) as s:
    s.connect(('127.0.0.1', 8080))
    s.sendall(header_bytes)
    sleep(1)
    s.sendall(body_bytes)
    sleep(1)
    print(s.recv(1024))
